repeating_items &lt;- map(
  rucksack_divided,
  function(compartments){
    unique_contents &lt;- map(
      compartments,
      function(item_list){
        strsplit(item_list, split = "") %&gt;%
          unlist() %&gt;%
          unique()
      }
    )
    return(Reduce(f = intersect, x = unique_contents))
