change_worry_level(thing, operation){
  old &lt;- thing$worry_level
  new &lt;- NA
  eval(parse_expr(operation))
  thing$worry_level &lt;- new
  return(thing)
}
change_monkey_holding(thing, old_monkey, new_monkey){
  remove_thing(old_monkey, thing$id)
  add_thing(new_monkey, thing$id)
  thing$monkey &lt;- new_monkey$id
  return(thing)
}

Check who wins per round
I get 0 if I lose, 3 if it‚Äôs a draw, and 6 if I win.

#' returns points for player1
check_rules &lt;- function(player1, player2){
  player1_lose &lt;- 0
  player1_draw &lt;- 3
  player1_win &lt;- 6

  all_diff &lt;- player1 - player2
  map_dbl(all_diff, function(cur_diff){
    if(cur_diff == 0){ # draw
      return(player1_draw)
    }

    if(abs(cur_diff) == 2){ # one of them has 'rock', one 'scissors'
      if(cur_diff &lt; 0){
        return(player1_win)
      }

      return(player1_lose)
    }

    if(cur_diff &gt; 0){ # check who has higher value
      return(player1_win)
    }

    return(player1_lose)
  })
}

