all_no_draws = [12, 13, 14];

function get_games_not_possible(color, no_draws)
    cur_subset = subset(
        all_games,
        :colors =&gt; col -&gt; col .== color,
        :draws =&gt; dr -&gt; dr .&gt; no_draws
    );
    return unique(cur_subset.game_no)
end

